import React, { useEffect, useState } from 'react';
import { apiRequest } from '../utils/api';
import PostCard from '../components/PostCard';
import { MagnifyingGlassIcon } from '@heroicons/react/24/outline';

function Home() {
  const [posts, setPosts] = useState([]);
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    apiRequest(`/api/posts?search=${search}&page=${page}&limit=9`)
      .then(data => {
        setPosts(data.posts);
        setTotal(data.total);
      })
      .catch(() => setPosts([]))
      .finally(() => setLoading(false));
  }, [search, page]);

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-2xl mx-auto mb-8">
        <h1 className="text-4xl font-bold text-gray-800 text-center mb-8">
          Discover Amazing Stories
        </h1>
        <div className="relative">
          <MagnifyingGlassIcon className="h-5 w-5 text-gray-400 absolute left-3 top-1/2 transform -translate-y-1/2" />
          <input
            type="text"
            placeholder="Search by title or author..."
            value={search}
            onChange={e => setSearch(e.target.value)}
            className="w-full pl-10 pr-4 py-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-primary-500 focus:border-transparent"
          />
        </div>
      </div>

      {loading ? (
        <div className="flex justify-center items-center min-h-[300px]">
          <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary-600"></div>
        </div>
      ) : posts.length === 0 ? (
        <div className="text-center text-gray-600 min-h-[300px] flex items-center justify-center">
          <p className="text-xl">No posts found. Try a different search term.</p>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {posts.map(post => (
            <PostCard key={post._id} post={post} />
          ))}
        </div>
      )}

      <div className="mt-8 flex justify-center items-center space-x-4">
        <button 
          disabled={page === 1} 
          onClick={() => setPage(page - 1)} 
          className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
            page === 1 
              ? 'bg-gray-100 text-gray-400 cursor-not-allowed' 
              : 'bg-primary-600 text-white hover:bg-primary-500'
          }`}
        >
          Previous
        </button>
        <span className="text-gray-600">Page {page}</span>
        <button 
          disabled={page * 9 >= total} 
          onClick={() => setPage(page + 1)} 
          className={`px-4 py-2 rounded-md text-sm font-medium transition-colors ${
            page * 9 >= total 
              ? 'bg-gray-100 text-gray-400 cursor-not-allowed' 
              : 'bg-primary-600 text-white hover:bg-primary-500'
          }`}
        >
          Next
        </button>
      </div>
      
      {total > 0 && (
        <p className="text-center text-gray-600 mt-4">
          Showing {Math.min(page * 9, total)} of {total} posts
        </p>
      )}
    </div>
  );
}

export default Home;
