import React, { useEffect, useState } from 'react';
import { apiRequest } from '../utils/api';
import { Link } from 'react-router-dom';

function Home() {
  const [posts, setPosts] = useState([]);
  const [search, setSearch] = useState('');
  const [page, setPage] = useState(1);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    setLoading(true);
    apiRequest(`/api/posts?search=${search}&page=${page}&limit=10`)
      .then(data => {
        setPosts(data.posts);
        setTotal(data.total);
      })
      .catch(() => setPosts([]))
      .finally(() => setLoading(false));
  }, [search, page]);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Blog Feed</h1>
      <input
        type="text"
        placeholder="Search by title or author"
        value={search}
        onChange={e => setSearch(e.target.value)}
        className="border p-2 mb-4 w-full"
      />
      {loading ? <p>Loading...</p> : posts.length === 0 ? <p>No posts found.</p> : (
        <div className="grid gap-4">
          {posts.map(post => (
            <Link key={post._id} to={`/post/${post._id}`} className="block border p-4 rounded hover:shadow">
              <div className="flex items-center">
                {post.imageURL && <img src={post.imageURL} alt="" className="w-24 h-16 object-cover mr-4" />}
                <div>
                  <h2 className="font-semibold text-lg">{post.title}</h2>
                  <p className="text-sm text-gray-600">By {post.username} â€¢ {new Date(post.createdAt).toLocaleDateString()}</p>
                </div>
              </div>
            </Link>
          ))}
        </div>
      )}
      <div className="mt-4 flex gap-2">
        <button disabled={page === 1} onClick={() => setPage(page - 1)} className="px-3 py-1 border rounded">Prev</button>
        <span>Page {page}</span>
        <button disabled={page * 10 >= total} onClick={() => setPage(page + 1)} className="px-3 py-1 border rounded">Next</button>
      </div>
    </div>
  );
}

export default Home;
