import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { apiRequest } from '../utils/api';

function PostDetail() {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    apiRequest(`/api/posts/${id}`)
      .then(setPost)
      .catch(() => setPost(null))
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) return <p>Loading...</p>;
  if (!post) return <p>Post not found.</p>;

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-2">{post.title}</h1>
      {post.imageURL && <img src={post.imageURL} alt="" className="w-full max-w-xl mb-4" />}
      <p className="mb-2 text-gray-600">By {post.username} â€¢ {new Date(post.createdAt).toLocaleDateString()}</p>
      <div className="prose max-w-xl">{post.content}</div>
    </div>
  );
}

export default PostDetail;
