import React from 'react';
import { Link } from 'react-router-dom';
import { CalendarIcon, UserIcon } from '@heroicons/react/24/outline';

function PostCard({ post }) {
  return (
    <div className="bg-white rounded-lg shadow-md overflow-hidden transition-transform duration-300 hover:scale-[1.02] hover:shadow-lg">
      {post.imageURL && (
        <img
          className="w-full h-48 object-cover"
          src={post.imageURL}
          alt={post.title}
        />
      )}
      <div className="p-6">
        <h2 className="text-xl font-semibold text-gray-800 mb-2">
          {post.title}
        </h2>
        <p className="text-gray-600 mb-4 line-clamp-3">
          {post.content}
        </p>
        <div className="flex items-center text-sm text-gray-500 space-x-4">
          <div className="flex items-center">
            <UserIcon className="h-4 w-4 mr-1" />
            <span>{post.username}</span>
          </div>
          <div className="flex items-center">
            <CalendarIcon className="h-4 w-4 mr-1" />
            <span>{new Date(post.createdAt).toLocaleDateString()}</span>
          </div>
        </div>
        <Link
          to={`/post/${post._id}`}
          className="mt-4 inline-block bg-primary-600 text-white px-4 py-2 rounded-md text-sm font-medium hover:bg-primary-500 transition-colors"
        >
          Read More
        </Link>
      </div>
    </div>
  );
}

export default PostCard;
