import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import { apiRequest } from '../utils/api';
import { CalendarIcon, UserIcon, ArrowLeftIcon } from '@heroicons/react/24/outline';

function PostDetail() {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    apiRequest(`/api/posts/${id}`)
      .then(setPost)
      .catch(() => setPost(null))
      .finally(() => setLoading(false));
  }, [id]);

  if (loading) {
    return (
      <div className="flex justify-center items-center min-h-screen">
        <div className="animate-spin rounded-full h-12 w-12 border-t-2 border-b-2 border-primary-600"></div>
      </div>
    );
  }

  if (!post) {
    return (
      <div className="container mx-auto px-4 py-16 text-center">
        <h1 className="text-2xl font-bold text-gray-800 mb-4">Post not found</h1>
        <p className="text-gray-600 mb-8">The post you're looking for doesn't exist or has been removed.</p>
        <Link
          to="/"
          className="inline-flex items-center text-primary-600 hover:text-primary-500"
        >
          <ArrowLeftIcon className="h-5 w-5 mr-2" />
          Back to Home
        </Link>
      </div>
    );
  }

  return (
    <article className="container mx-auto px-4 py-8">
      <Link
        to="/"
        className="inline-flex items-center text-gray-600 hover:text-primary-600 mb-8"
      >
        <ArrowLeftIcon className="h-5 w-5 mr-2" />
        Back to Posts
      </Link>
      
      <div className="max-w-3xl mx-auto">
        <h1 className="text-4xl font-bold text-gray-900 mb-6">{post.title}</h1>
        
        <div className="flex items-center space-x-6 text-sm text-gray-500 mb-8">
          <div className="flex items-center">
            <UserIcon className="h-5 w-5 mr-2" />
            <span>{post.username}</span>
          </div>
          <div className="flex items-center">
            <CalendarIcon className="h-5 w-5 mr-2" />
            <span>{new Date(post.createdAt).toLocaleDateString('en-US', {
              year: 'numeric',
              month: 'long',
              day: 'numeric'
            })}</span>
          </div>
        </div>

        {post.imageURL && (
          <img
            src={post.imageURL}
            alt={post.title}
            className="w-full aspect-video object-cover rounded-lg shadow-lg mb-8"
          />
        )}

        <div className="prose prose-lg max-w-none">
          {post.content}
        </div>
      </div>
    </article>
  );
}

export default PostDetail;
